import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import { useState } from "react";
import "./Expenses.css";
import Card from "../UI/Card";

export default function Expenses(props) {
  const [expenseYear, setExpenseYear] = useState("2020");
  const [filterInfoText, setfilterInfoText] = useState("2019, 2020 and 2022");

  const filterHandler = (year) => {
    setExpenseYear(year);
  };

  // useEffect(() => {
  //   console.log("From expenses.js:");
  //   console.log(expenseYear);
  // }, [expenseYear]);

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter handler={filterHandler} selectedYear={expenseYear}/>
        <p>Data for years {filterInfoText} are hidden.</p>
        {props.expenses.map((expense) => (
          <ExpenseItem
            key={expense.id}
            name={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
      </Card>
    </div>
  );
}
